// SignUp.js
import React from 'react';
import './SignUp.css'; // Ensure the CSS file is correctly placed in the src directory

const SignUp = () => {
  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    const signUpData = {
      username: formData.get('username'),
      password: formData.get('password'),
      name: formData.get('name'),
      email: formData.get('email'),
      address: formData.get('address'),
      phoneNumber: formData.get('phoneNumber'),
    };

    // Example POST request using fetch
    fetch('http://localhost:8080/customers/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(signUpData),
    })
      .then((response) => {
        if (response.ok) {
          console.log('Registration successful');
          // Handle successful registration, e.g., redirect to sign-in
        } else {
          console.error('Registration failed');
          // Handle registration failure
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div className="container">
      <header>
        <h2>Sign Up</h2>
      </header>

      <form className="form" onSubmit={handleSubmit}>
        {/* Form fields */}
        <button type="submit">Sign Up</button>
      </form>
      <a href="/signin" className="SignUp-link">Already have an account? Sign In</a>
    </div>
  );
};

export default SignUp;
